package com.dumpster_sensor.webapp.models;

import javax.persistence.*;

@Entity
@Table(name = "sensor")

public class Sensor {
    //PK. Autogenerated in DB
    @Id
    @Column(nullable = false, unique = true)
    @GeneratedValue
    private Long id;

    @Column(nullable = false, length = 45)
    private String location;

    @Column(nullable = false, length = 45)
    private String installed;

    //not sure if this needs to be a string but for now it is a string
    @Column
    private String power;

    @Column
    private String lastUpdated;

    public Sensor() {
    }

    public Sensor(Long id, String location, String installed, String power, String lastUpdated) {
        this.id = id;
        this.location = location;
        this.installed = installed;
        this.power = power;
        this.lastUpdated = lastUpdated;
    }

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getLocation() {
        return location;
    }

    public void setLocation(String location) {
        this.location = location;
    }

    public String getInstalled() {
        return installed;
    }

    public void setInstalled(String installed) {
        this.installed = installed;
    }

    public String getPower() {
        return power;
    }

    public void setPower(String power) {
        this.power = power;
    }

    public String getLastUpdated() {
        return lastUpdated;
    }

    public void setLastUpdated(String lastUpdated) {
        this.lastUpdated = lastUpdated;
    }
}
